package com.intuit.test.planetary.utils;import static org.testng.Assert.fail;import java.io.PrintWriter;import java.io.Writer;import java.io.StringWriter;import java.util.logging.Logger;import org.testng.Reporter;/** * The Class LogUtils. */public final class LogUtils {    /**     * The message logger for this class.     */    private static Logger logger = Logger.getLogger(LogUtils.class.getName());    /**     * Converts a stack trace to a string.     *     * @param e     *            the exception object     * @return the string representation of the stack trace     */    public static String convertStackTraceToString(Throwable e) {        Writer writer = new StringWriter();        PrintWriter printWriter = new PrintWriter(writer);        e.printStackTrace(printWriter);        return writer.toString();    }    /**     * Log info message.     *     * @param msg     *            the message to log     */    public static void logInfoMessage(String msg) {        logger.info(msg);        Reporter.log(msg);    }    /**     * Log severe message.     *     * @param msg     *            the message to log     */    public static void logSevereMessage(String msg) {        logger.severe(msg);        Reporter.log(msg);    }    /**     * Log severe message then fail.     *     * @param msg     *            the message to log     */    public static void logSevereMessageThenFail(String msg) {        logger.severe(msg);        Reporter.log(msg);        fail(msg);    }    /**     * Log warning message.     *     * @param msg     *            the message to log     */    public static void logWarningMessage(String msg) {        logger.warning(msg);        Reporter.log(msg);    }    /**     * Constructor to prevent instantiation from other classes.     */    private LogUtils() {    }}